import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const CriteriaForm = ({ setProducts }) => {
    const [criteria, setCriteria] = useState({
        cpu: '',
        ram: '',
        storage: '',
        maxPrice: ''
    });

    const handleChange = (e) => {
        setCriteria({
            ...criteria,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await axios.get('/api/products', {
            params: criteria
        });
        setProducts(response.data);
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                CPU:
                <input type="text" name="cpu" value={criteria.cpu} onChange={handleChange} />
            </label>
            <label>
                RAM:
                <input type="text" name="ram" value={criteria.ram} onChange={handleChange} />
            </label>
            <label>
                Storage:
                <input type="text" name="storage" value={criteria.storage} onChange={handleChange} />
            </label>
            <label>
                Max Price:
                <input type="number" name="maxPrice" value={criteria.maxPrice} onChange={handleChange} />
            </label>
            <button type="submit">Search</button>
        </form>
    );
};

export default CriteriaForm;
