import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import './App.css';
import CriteriaForm from './CriteriaForm';
import ConfigurationResult from './ConfigShow';

function Home() {
  const navigate = useNavigate();

  const handleStartClick = () => {
    navigate('/criteria');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Конфигуратор ИТ инфраструктуры в условиях импортозамещения</h1>
      </header>
      <div className="App-body">
        <button onClick={handleStartClick} className="Start-button">
          Старт
        </button>
      </div>
    </div>
  );
}

function App() {
  const [products, setProducts] = useState([]);
  const [configurationType, setConfigurationType] = useState('');
  const [sector, setSector] = useState('');
  const [performanceType, setPerformanceType] = useState('');

  const handleCriteriaSubmit = (data) => {
    setProducts(data.products);
    setConfigurationType(data.selectionType);
    setSector(data.sectors);
    setPerformanceType(data.configType);
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/criteria" element={<CriteriaForm onSubmit={handleCriteriaSubmit} />} />
        <Route path="/result" element={<ConfigurationResult
          products={products}
          configurationType={configurationType}
          sector={sector}
          performanceType={performanceType}
        />} />
      </Routes>
    </Router>
  );
}

export default App;
