import React from 'react';
import './ConfigShow.css';

const ConfigurationResult = ({ products, configurationType, sectors, performanceType }) => {
  // Фильтрация продуктов на основе выбранных критериев
  const filteredProducts = products.filter(product => {
    if (configurationType !== 'both' && product.type !== configurationType) return false;
    if (!product.sectors.some(sector => sectors.includes(sector) || sector === 'all')) return false;
    if (product.performance !== performanceType) return false;
    return true;
  });

  // Классификация продуктов по категориям
  const categories = {
    os: 'Операционные системы',
    office: 'Офисные пакеты',
    antivirus: 'Антивирусное ПО',
    special: 'Специальное ПО',
    pc: 'Персональные компьютеры',
    server: 'Серверы',
    storage: 'Системы хранения данных',
    router: 'Маршрутизаторы',
    switch: 'Коммутаторы'
  };

  const categorizedProducts = {
    os: [],
    office: [],
    antivirus: [],
    special: [],
    pc: [],
    server: [],
    storage: [],
    router: [],
    switch: []
  };

  filteredProducts.forEach(product => {
    categorizedProducts[product.equipmentType].push(product);
  });

  return (
    <div className="configuration-result">
      {Object.keys(categories).map(category => (
        <div key={category} className="category">
          <h2>{categories[category]}</h2>
          {categorizedProducts[category].length > 0 ? (
            categorizedProducts[category].map(product => (
              <div key={product._id} className="product-card">
                <img src={product.image} alt={product.name} className="product-image" />
                <h2>{product.name}</h2>
                <p>{product.description}</p>
                {product.type === 'hardware' && (
                  <ul className="specs">
                    {Object.entries(product.specs).map(([key, value]) => (
                      <li key={key}>{key}: {value}</li>
                    ))}
                  </ul>
                )}
                <a href={product.link} target="_blank" rel="noopener noreferrer" className="details-button">Подробнее</a>
              </div>
            ))
          ) : (
            <p>Не найдено продуктов в категории {categories[category]}.</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default ConfigurationResult;
